"use client"

import { ParticleBackground } from "../pioneerui/particle-background";

export default function ParticleBubblesDemo() {
  return (
    <div className="relative flex h-[500px] w-full flex-col items-center justify-center overflow-hidden rounded-lg border bg-[#0D001A]">
      <span className="pointer-events-none z-10 whitespace-pre-wrap bg-gradient-to-br from-[#FF6A00] via-[#EE0979] to-[#FF6A00] bg-clip-text text-center text-7xl font-bold leading-none tracking-tighter text-transparent">
        Particle Background - Bubbles
      </span>
      <ParticleBackground
        particleCount={100}
        preset="bubbles"
        color="#FF6A00"
        speed={2}
      />
    </div>
  );
}
